import axios from "axios";
import {
  View,
  Text,
  Image,
  Pressable,
  TouchableOpacity,
  Button,
  Dimensions,
} from "react-native";
import React, { useEffect, useState } from "react";
import { SafeAreaView } from "react-native-safe-area-context";
import * as ImagePicker from "expo-image-picker";
import * as FileSystem from "expo-file-system";
import * as Clipboard from 'expo-clipboard';
import {
  Ionicons,
  FontAwesome5,
  MaterialCommunityIcons,
  Entypo
} from "@expo/vector-icons";
import {
  GestureHandlerRootView,
  ScrollView,
  TextInput,
} from "react-native-gesture-handler";
import ImageSelect from "../components/ImageSelect";
import Toast from "react-native-toast-message";
import { api_url } from "../config";
import { useSelector } from "react-redux";

const Btc = ({ navigation }) => {
  const [depositAmount, setDepositAmount] = useState(0);

  // _______________________________________

  const [image, setImage] = useState(null);
  // const { userReducer } = useSelector((state) => state);
  const currentUser = useSelector(
    (state) => state?.userReducer?.currentUser
  );
  console.log("CURRENT USDTTRc", currentUser)
  const data = {
    userId: currentUser?._id,
    username: currentUser?.username,
    amount: depositAmount,
    currency: 'BTC',
  };
  const depositData = JSON.stringify(data);

  const openImagePickerAsync = async () => {
    let permissionResult =
      await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (permissionResult.granted === false) {
      alert('Permission to access camera roll is required!');
      return;
    }
    let pickerResult = await ImagePicker.launchImageLibraryAsync();
    console.
    setImage(pickerResult?.assets[0].uri);
  };

  const handleDeposit = async () => {
    if (!depositAmount || !image) {
      Toast.show({
        type: 'error',
        text1: 'Error',
        text2: 'Please enter the deposit amount and select an image.',
      });
      return;
    } else {
      try {
        const formData = new FormData();
        // Append the image file to FormData
        formData.append('demo_image', image);
        // Append other data to the FormData object
        // Append depositData (if needed) to the FormData object
        formData.append('depositData', JSON.stringify(depositData));

        const response = await axios.post(`${api_url}/deposit/createDeposit`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            // You might need other headers depending on your server requirements
          },
        });

        // Handle the response from the server
        console.log(response.data); // Log the response from the server
        if (response.status === 200) {
          Toast.show({
            type: 'success',
            text1: 'Success',
            text2: 'Deposit request sent successfully.',
          });
          setImage(null);
          setDepositAmount(null);
          console.log('Response:', response.data);
        } else {
          Toast.show({
            type: 'error',
            text1: 'Error',
            text2: `${response.data?.error?.message}`,
          });
          console.log('Error Response:', response.data);
        }
      } catch (error) {
        console.error('Error:', error);
        Toast.show({
          type: 'error',
          text1: 'Error',
          text2: 'Failed to send deposit request.',
        });
      }
    }
  };


  const [copiedText, setCopiedText] = React.useState('');

  const copyToClipboard = async () => {
    await Clipboard.setStringAsync('BTC');
  };

  return (
    <GestureHandlerRootView>
      <SafeAreaView style={{
        backgroundColor: "white"
      }}>
        <ScrollView>
          <View
            style={{
              flex: 1,
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <View
              style={{
                flexDirection: 'row',
                alignItems: 'center',
              }}
            >
              <Ionicons
                name="arrow-back-outline"
                size={30}
                color="black"
                style={{
                  padding: 5,
                  marginLeft: 10,
                  marginTop: 10,
                }}
                onPress={() => navigation.navigate("Deposit")}
              />
              <Text
                style={{
                  padding: 5,
                  marginLeft: 5,
                  marginTop: 10,
                  fontSize: 20,
                  fontWeight: "bold",
                }}
              >
                Deposit
              </Text>
            </View>
            <View
              style={{
                flexDirection: 'row',
                justifyContent: 'space-between',
              }}
            >
            </View>
            <View
              style={{
                flexDirection: 'row',
                justifyContent: 'space-between',
              }}
            >
              <View
                style={{
                  flexDirection: 'row',
                  alignItems: 'center',
                }}
              >
                <FontAwesome5
                  name="history"
                  size={25}
                  color="black"
                  style={{
                    padding: 5,
                    marginLeft: 240,
                    marginRight: 20
                  }}
                  onPress={() => navigation.navigate("Deposithistory")}
                />
              </View>


            </View>
          </View>
          <View
            style={{
              flexDirection: "row",
              marginTop: 40,
              justifyContent: "space-between",
              alignItems: "center",
              paddingHorizontal: 19,
            }}
          >
            <Text
              style={{
                fontSize: 17,
                color: "gray",
              }}
            >
              Network
            </Text>
            <View
              style={{
                flexDirection: 'row',
                justifyContent: 'space-between',
              }}
            >
              <View
                style={{
                  flexDirection: 'row',
                  alignItems: 'center',
                }}
              >
                <Image
                  source={require("../assets/BTC.png")}
                  style={{
                    alignItems: "center",
                    height: 28,
                    width: 28,
                    marginLeft: 190,
                    marginRight: 10,
                  }}
                />

                <Text
                  style={{
                    fontSize: 20,
                    fontWeight: "600",
                    color: "black",
                  }}
                >
                  BTC
                </Text>
              </View>


            </View>
          </View>

          <View
            style={{
              justifyContent: "center",
              alignItems: "center",
              marginBottom: 20,
            }}
          >
            <Image
              source={require("../assets/(new)ethqrcode.jpg")}
              style={{
                borderColor: "aqua",
                borderWidth: 2,
                width: 200,
                height: 200,
                marginTop: 20,
              }}
            />
            <Text
              style={{
                padding: 25,
                textAlign: "center",
                fontSize: 18,
                color: "gray",
                textDecorationLine: "underline",
              }}
              selectable={true}
            >
              0x4A1f2938AB5a0250E07e38f9a91774880Cef1C6d
            </Text>
            <TouchableOpacity
              style={{
                flexDirection: "row",
                justifyContent: "center",
                alignItems: "center",
                padding: 10,
                backgroundColor: "#0e1111",
                borderRadius: 25,
                width: 200,
              }}
              onPress={copyToClipboard}
            >
              <Text style={{ color: "white", marginRight: 5 }}>Copy</Text>
              <Ionicons name="ios-copy" size={24} color="white" />
            </TouchableOpacity>
          </View>
          <View style={{
            marginLeft: "3%"
          }}>
            <Text
              style={{
                fontSize: 15,
                color: "gray",
              }}
            >
              Deposit Amount
            </Text>
          </View>
          <View
            style={{
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <GestureHandlerRootView>
              <TextInput
                style={{
                  borderWidth: 1,
                  width: 400,
                  padding: 15,
                  marginTop: 10,
                }}
                placeholder="Please Enter Amount"
                value={depositAmount}
                onChangeText={(text) => setDepositAmount(text)}
                keyboardType="numeric"
              />
            </GestureHandlerRootView>
          </View>
          <View
            style={{
              marginLeft: "3%",
              marginTop: 20,
            }}
          >
            <Text
              style={{
                fontSize: 15,
                color: "gray",
                paddingBottom: 15
              }}
            >
              Click to upload pictures
            </Text>
          </View>
          <Pressable
            style={{
              alignSelf: "center",
              justifyContent: "center",
              alignItems: "center",
              paddingVertical: 20,
              width: "94%",
              backgroundColor: "#0e1111",
            }} onPress={() => openImagePickerAsync()}
          >
            {image && (
              <Image
                source={{ uri: image }}
                style={{ width: 150, height: 150 }}
              />
            )}
            <View style={{ flex: 1, borderColor: "white", justifyContent: "center", padding: 30, borderRadius: 15, borderWidth: 2, borderStyle: "dashed" }}>
              <Entypo name="camera" size={30} color="white" alignSelf="center" />
            </View>
          </Pressable>
          <TouchableOpacity
            style={{
              flexDirection: "row",
              justifyContent: "center",
              alignItems: "center",
              alignSelf: "center",
              padding: 10,
              backgroundColor: "aqua",
              borderRadius: 25,
              width: "100%",
              marginTop: 20,
              marginBottom: "5%",
            }}
            onPress={handleDeposit}
          >
            <Text style={{ color: "white", marginRight: 5 }}>Confirm recharge</Text>
            <Ionicons name="ios-send" size={24} color="white" />
          </TouchableOpacity>
        </ScrollView>
      </SafeAreaView>
    </GestureHandlerRootView>
  );
};

export default Btc;
