import React, { useEffect, useState, userReducer } from "react";
import {
  View,
  Text,
  Pressable,
  TouchableOpacity,
  StyleSheet,
  ActivityIndicator,
} from "react-native";
import {
  DrawerContentScrollView,
  DrawerItemList,
} from "@react-navigation/drawer";
import { FontAwesome, MaterialIcons } from "@expo/vector-icons";
import { useDispatch, useSelector } from "react-redux";
import { userLogOut } from "../stores/user/userActions";

const CustomDrawer = (props) => {
  // const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();
  const currentUser = useSelector(
    (state) => state?.userReducer?.currentUser?.username
  );


  useEffect(() => {
    if (!currentUser?.username) {
      props.navigation.closeDrawer();

    }
  }, [currentUser]);

  // const signOut = () => {
  //   dispatch(userLogOut());
  // };

  const signOut = () => {
    // setLoading(true);
    dispatch(userLogOut());
    // props.navigation.navigate("Home2");
  };
  return (
    <View
      style={{
        flex: 1,
      }}
    >
      <DrawerContentScrollView
        {...props}
        contentContainerStyle={{
          backgroundColor: "white",
        }}
      >
        <View
          style={{
            flexDirection: "row",
          }}
        >
          <FontAwesome
            name="user-circle"
            size={65}
            color="black"
            style={{
              padding: 20,
            }}
          />
          <View style={{ flexDirection: "column" }}>
            <Text
              style={{ color: "black", paddingTop: 25, fontWeight: "bold" }}
            >
              {currentUser}
            </Text>
            {/* <Text style={{ color: "gray" }}>Credit Score: 70</Text> */}
            <View
              style={{
                flexDirection: "row",
                alignContent: "center",
                alignItems: "center",
              }}
            >
              <MaterialIcons
                name="verified"
                size={20}
                // color={userReducer?.currentUser?.isVerified ? "lime":"gray"}
                color="lime"
              />
              <Text
                // style={ userReducer?.currentUser?.isVerified ? styles.verifiedStyle : styles.notVeirfiedStyle }
                style={styles.verifiedStyle}
              >
                {/* {userReducer?.currentUser?.isVerified
                  ? "Verified"
                  : "Not verified"} */}
                Verified
              </Text>
            </View>
            {/* { !userReducer?.currentUser?.isVerified && <TouchableOpacity >
               <Text style={{fontSize:12, textDecorationLine:"underline"}}>
                Click here to verify
               </Text>
              </TouchableOpacity>} */}
          </View>
        </View>
        <DrawerItemList {...props} />
      </DrawerContentScrollView>
      <View style={{ padding: 20, borderTopWidth: 1, borderTopColor: "black" }}>
        <TouchableOpacity
          style={{
            padding: 20,
            borderRadius: 20,
            borderWidth: 2,
            backgroundColor: "black",
          }}
          onPress={() => signOut()}
        >
          {/* {loading ? (
            <ActivityIndicator size="small" color="white" />
          ) : ( */}
            <Text
              style={{
                color: "white",
                alignSelf: "center",
                fontSize: 15,
                fontWeight: "bold",
              }}
            >
              Sign out
            </Text>
          {/* )} */}
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default CustomDrawer;

const styles = StyleSheet.create({
  verifiedStyle: {
    color: "lime",
    fontWeight: "bold",
    fontSize: 13,
    textTransform: "uppercase",
    paddingLeft: 2,
  },
  notVeirfiedStyle: {
    color: "red",
    fontWeight: "bold",
    fontSize: 13,
    textTransform: "uppercase",
    paddingLeft: 2,
  },
});
