import { View, Text, TouchableOpacity, FlatList, Image, Animated, StyleSheet, Pressable } from 'react-native';
import React from 'react';
import { connect } from 'react-redux';
import { getCoinMarket } from '../stores/market/marketActions';
import { style } from 'deprecated-react-native-prop-types/DeprecatedViewPropTypes';
import constants from '../constants/constants';
import { withExtraArgument } from 'redux-thunk';
import { LineChart } from 'react-native-chart-kit';
import { SafeAreaView } from 'react-native-safe-area-context';
import { MaterialCommunityIcons, AntDesign, Feather} from '@expo/vector-icons';
import { TextInput} from 'react-native-gesture-handler';
import { ScrollView } from 'react-native-virtualized-view';

const marketTabs = constants.marketTabs.map((marketTab) => ({
    ...marketTab,
    ref: React.createRef()

}))

const Markets = ({ getCoinMarket, coins, state, navigation}) =>
{

    const scrollX = React.useRef(new Animated.Value(0)).current;

    React.useEffect(() =>
    {
        getCoinMarket()


    }, [])
function renderHeader() {
    return( 
        <ScrollView style={{
            backgroundColor: 'white',
            padding: 15,
            paddingTop: 60
        }}>
            <View style={{
                flex: 1,
                flexDirection: 'row',
                justifyContent: 'space-between',


            }}>
               <Pressable onPress={() => navigation.openDrawer()}><MaterialCommunityIcons name="dots-grid" size={45} color="black" /></Pressable> 
                <View style={{
                    flexDirection: 'row',
                    justifyContent: 'center',
                    borderColor: 'gray',
                    borderWidth: 1,
                    borderRadius: 20,
                    paddingHorizontal: 70,
                    alignItems: 'center'
                }}>
                <AntDesign name="search1" size={24} color="gray" style={{marginRight: 5}}/>
                <TextInput placeholder='Search coin pairs'></TextInput></View>

                <AntDesign name="customerservice" size={40} color="black" />
           </View>
           <View
        style={{flexDirection: 'row', paddingHorizontal: 10, paddingVertical: 10, justifyContent: 'space-evenly', alignItems: 'center', alignContent: 'center', paddingBottom: 5, paddingTop: 20}}>
            <TouchableOpacity style={{ backgroundColor: "#000000", borderRadius: 20, padding: 10, paddingHorizontal: 20}}>
            <Text style={{fontSize: 13, lineHeight: 21, fontWeight: 'bold', letterSpacing: 0.25,color: 'white'}}>Optional</Text></TouchableOpacity>

            <TouchableOpacity style={{backgroundColor: "#000000", borderRadius: 20, padding: 10, paddingHorizontal: 20}}>
                <Text style={{ textAlign: 'center', fontSize: 13, lineHeight: 21, fontWeight: 'bold', letterSpacing: 0.25, color: 'white'}}>Options</Text></TouchableOpacity>

            <TouchableOpacity style={{backgroundColor: "#000000", borderRadius: 20, padding: 10, paddingHorizontal: 20}}>
                <Text style={{textAlign: 'center', fontSize: 13, lineHeight: 21, fontWeight: 'bold', letterSpacing: 0.25, color: 'white'}}>Spot</Text></TouchableOpacity>
            <TouchableOpacity style={{backgroundColor: "#000000", borderRadius: 20, padding: 10, paddingHorizontal: 20}}>
                <Text style={{textAlign: 'center', fontSize: 13, fontWeight: 'bold', letterSpacing: 0.25, color: 'white'}}>USDT-M</Text></TouchableOpacity>
        </View>
           </ScrollView>
    )
}

    function renderList() {
        return (
            <Animated.FlatList data={marketTabs} contentContainerStyle={{marginTop: 1, width: "100%"}} 
                horizontal
                pagingEnabled
                scrollEventThrottle={16}
                snapToAlignment='center'
                showsHorizontalScrollIndicator={false}
                keyExtractor={item => item.id}
                onScroll={
                    Animated.event([
                        { nativeEvent: { contentOffset: { x: scrollX } } }
                    ], {
                        useNativeDriver: false
                    })
                }
                renderItem={({item, index}) => {
                    return (
                        <View style={{
                            flex: 1,
                            width: 20
                        }}

                        >

                        </View>

                    )
                }}/>
        )
    }

    return (    
        <View>
        {renderHeader()}
        <ScrollView style={{
            backgroundColor: 'white'
        }}>

            <View
                style={{
                    flex: 1, 
                flexDirection: 'row',
                justifyContent: 'center',
                    width: '100%',
                    height: '100%',
                    backgroundColor: 'white',
                    paddingBottom: 200
                }}
            > 
                <FlatList
                    data={coins}
                    keyExtractor={item => item.id}
                    renderItem={({ item, index }) =>
                    {

                        let priceColor = (item.price_change_percentage_7d_in_currency == 0 ? "grey" : (item.price_change_percentage_7d_in_currency > 0) ? "green" : "red")

                        return (
                            <View
                                style={{
                                    flexDirection: 'row',
                                    paddingHorizontal: 25,
                                    marginBottom: 15,
                                    marginTop: 15,
                                    alignSelf: 'center',
                                    alignItems: 'center'

                                }}
                            >
                                {/*coins*/}
                                <Image
                                source={{ uri: item.image }}
                                style={{
                                    height: 35,
                                    width: 35,
                                }}
                            />
                                <View style={{
                                        flex: 1,
                                        flexDirection: 'column',
                                        alignItems: 'center',
                                        alignSelf: 'center',
                                    }}
                                >

                                    <Text 
                                    style= {{
                                        color: "black",
                                        fontSize: 15,
                                        textTransform: "uppercase",
                                        fontWeight: 'bold'

                                    }}
                                    >{item.symbol}USDT</Text>


                                    <View style={{
                                        flexDirection: "row",
                                        justifyContent: "flex-center",
                                        alignItems: "center",
                                        alignSelf: 'center',

                                    }}>
                                    <Text style={{
                                        textTransform: "uppercase",
                                        color: "gray",
                                        marginRight: 5,
                                        alignItems: "center"
                                    }}>{item.symbol}</Text>
                                    <Text
                                     style={{
                                        paddingHorizontal: 7,
                                        backgroundColor: 'aqua',
                                        textAlign: 'center'
                                     }}>
                                        10X
                                    </Text>
                                    </View>

                                </View>
                                {/*linechart*/}
                                {/*linechart*/}
                                <View 
                                style={{
                                    flex: 1,
                                    alignItems: 'center',
                                    alignSelf: 'center',
                                    alignItems: 'center'
                                }}
                                >
                                    <LineChart
                                        withVerticalLabels={false}
                                        withHorizontalLabels={false}
                                        withDots={false}
                                        withInnerLines={false}
                                        withVerticaLines={false}
                                        withOutlerLines={false}
                                        withShadow={false}
                                        data={{
                                            datasets: [
                                                {
                                                    data: item.sparkline_in_7d.price
                                                }
                                            ]
                                        }}
                                        width={100}
                                        height={40}
                                        chartConfig={{
                                            color: () => priceColor,
                                            backgroundGradientFromOpacity: 0,
                                             backgroundGradientToOpacity: 0,
                                             propsForBackgroundLines: {
                                             strokeDasharray: "4",
                                                strokeWidth: 0, // If you put 0 in the value no line is displayed
                                                strokeDasharray: "4",
                                                strokeWidth: 0, // If you put 0 in the value no line is displayed
                                                stroke: `rgba(0, 0, 0, 0)`,
                                              },
                                        }}
                                        bezier
                                        style={{
                                            paddingRight: 0

                                        }}
                                    /> 
                                    </View>

                                {/*figures*/}
                                <View
                                style={{
                                    flex: 1,
                                    alignItems: "center",
                                    alignSelf: 'center',
                                    justifyContent: "flex-end",

                                }}
                                >
                                    <Text
                                style={{
                                    color: 'black',
                                    fontSize: 20,
                                    fontWeight: 'bold'
                                }}>
                                    $ {item.current_price}
                                </Text>

                                <View style={{
                                    flexDirection: 'row',
                                    alignSelf: 'center',
                                    alignItems: 'center'
                                }}
                                >
                                {
                                    item.price_change_percentage_7d_in_currency !=0 &&
                                    <Feather
                                    name="arrow-up"
                                    size={15}
                                    style={{
                                        color: priceColor,
                                        transform: item.price_change_percentage_7d_in_currency > 0 ? [{rotate: '45deg'}] : [{rotate: '125deg'}]
                                    }}
                                  />
                                }
                                <Text
                                    style={{
                                        marginLeft: 5,
                                        color: priceColor,
                                        fontWeight: 'bold'
                                    }}
                                    > {item.price_change_percentage_7d_in_currency.toFixed(2)}%</Text>
                                    </View>
                                </View>
                                {/*figures*/}
                            </View>

                        )
                    }}
                    style={{ height: '100%', width: '100%' }}
                />
                </View>
                </ScrollView>
                </View>

                )
}

function mapStateToProps(state)
{
    return {
        coins: state.marketReducer.coins,
        state: state
    }
}


function mapDispatchToProps(dispatch)
{
    return {
        getCoinMarket: (currency, coinList, orderBy, sparkline, priceChangePerc, perPage,
            page) => { return dispatch(getCoinMarket(currency, coinList, orderBy, sparkline, priceChangePerc, perPage, page)) }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Markets)

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },

    viewmarket: {
        flex: 1,

    },

    tabtextstyle: {
        color: 'white'
    }

})
