      case 'bitcoin':
        currency = 'BTC';
        break;
      case 'ethereum':
        currency = 'ETH';
        break;
      case 'tether':
        currency = 'USDT-TRC';
        break;
      case 'usd-coin':
        currency = 'USDT-ERC';
        break;
