            <Ionicons name="arrow-back-outline" size={30} color="white" />
          </TouchableOpacity>
          <Text
            style={{
              fontSize: 18,
              fontWeight: 'bold',
              marginLeft: '4%',
              color: selectTab === 'Option' ? 'white' : 'gray',
            }}
            onPress={() => setSelectTab('Option')}
          >
