import {
  SafeAreaView,
  View,
  Text,
  Pressable,
  Image,
  FlatList,
  TouchableOpacity,
} from 'react-native';
import React, { useEffect, useState } from 'react';
import { AntDesign, FontAwesome, Entypo, FontAwesome5, Ionicons } from '@expo/vector-icons';
import { ScrollView } from 'react-native-gesture-handler';
import { useSelector } from 'react-redux';
import { api_url } from '../config';
import axios from 'axios';
import { useFocusEffect, useNavigation } from '@react-navigation/native';

const Wallet = () => {
  const navigation = useNavigation()
  const [wallet, setWallet] = useState([]);
  const [toUsd, setToUsd] = useState([]);
  const [totalUsd, setTotalUsd] = useState(0)
  // const user = useSelector((state) => state?.userReducer?.currentUser?.token);
  const user = useSelector((state) => state?.userReducer?.currentUser?.token);
  const cuser = useSelector((state) => state?.userReducer?.currentUser);
console.log('cuser', JSON.stringify(cuser))
const currentUser = user;

  const handleNavigation = (screen) => {
    if (currentUser) {
      navigation.navigate(screen);
    } else {
      setShowModal(true);
    }
  };
  const closeModal = () => {
    setShowModal(false);
  };

  // useEffect(()=>{
  //   if(wallet.length > 0){
  //     let test = 0;
  //     for(let i = 0; i < wallet.length; i++){
  //       test += wallet[i].toUsd

  //     }
  //     setTotalUsd(test)
  //   }
  // }, wallet)

  const walletId = useSelector(
    (state) => state?.userReducer?.currentUser?.walletId?._id
  );
  const getWallet = async () => {
    try {
      const response = await axios.post(`${api_url}/user/getWallet`, {
        walletId,
      });
      if (response?.data?.status === 200) {
        let arr = response?.data?.wallet?.currentBalance.sort(
          (a, b) => b.amount - a.amount
        );
        btcUnit = arr.find((item)=> item?.name === "BTC")
        console.log(bt)
        const apiKey =
      "685419b6bedfb725bb6af07ed3dd6fef8f20a83f05c066d1eb20a10c563c7801";
    const fromBtc = await axios.get(`https://min-api.cryptocompare.com/data/price?fsym=btc&tsyms=usd&api_key=${apiKey}`)
    let totalAmount = 0;
        btcUnit.toUsd =  btcUnit.amount * fromBtc?.data.USD
        totalAmount+ btcUnit.toUsd


        usdttrcUnit = arr.find((item)=> item?.name === "USDT-TRC")
        usdttrcUnit.toUsd =  usdttrcUnit?.amount
        totalAmount+ usdttrcUnit.toUsd


        // setTotalUsd((totalUsd)=> totalUsd + usdttrcUnit.amount )


        ethUnit = arr.find((item)=> item?.name === "ETH")
        const fromEth = await axios.get(`https://min-api.cryptocompare.com/data/price?fsym=eth&tsyms=usd&api_key=${apiKey}`)
        ethUnit.toUsd =  ethUnit.amount * fromEth?.data.USD
        totalAmount+ ethUnit.toUsd



        usdtercUnit = arr.find((item)=> item?.name === "USDT-ERC")
        usdtercUnit.toUsd =  usdtercUnit.amount
        totalAmount+ usdtercUnit.toUsd


        const updateData = [btcUnit, usdtercUnit, usdttrcUnit, ethUnit]
        console.log("UPDATED",updateData, "TOTAL", totalAmount); 

        setWallet(updateData);

        // console.log(sumOfAll)

      }
    } catch (error) {
      console.log('EROOROROOROROOR', error);
    }
  };

  useFocusEffect(
    React.useCallback(() => {
      getWallet();
      const apiKey =
      "685419b6bedfb725bb6af07ed3dd6fef8f20a83f05c066d1eb20a10c563c7801";
    // const apiUrl = `https://min-api.cryptocompare.com/data/price?fsym=${selectedOption2.cId}&tsyms=${selectedOption.cId}&api_key=${apiKey}`;
      checkRates()
    }, [])
  );

  const checkRates=async()=>{
    const apiKey =
      "685419b6bedfb725bb6af07ed3dd6fef8f20a83f05c066d1eb20a10c563c7801";
    const fromBtc = await axios.get(`https://min-api.cryptocompare.com/data/price?fsym=btc&tsyms=usd&api_key=${apiKey}`)
    console.log("Respoonse for ratess___",fromBtc)
  }





  console.log('wallet', wallet);
  return (
    <SafeAreaView style={{
      backgroundColor: "white",
      height: "100%"
    }}>
    <ScrollView style={{
      backgroundColor: "white",
      height: "100%"
    }}>
   <View
        style={{
          flexDirection: "row",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <View
          style={{
            flexDirection: 'row',
            alignItems: 'center',
            paddingLeft: 10
          }}
        >
        <Text
          style={{
            padding: 5,
            marginLeft: 5,
            marginTop: 10,
            fontSize: 20,
            fontWeight: "300",
          }}
        >
          Overview
        </Text>
        </View>
        <View
        style={{
          flexDirection: 'row',
          justifyContent: 'space-between',
        }}
      >
        </View>
        <View
        style={{
          flexDirection: 'row',
          justifyContent: 'space-between',
        }}
      >
        <View
          style={{
            flexDirection: 'row',
            alignItems: 'center',
          }}
        >
           <FontAwesome5
          name="history"
          size={25}
          color="black"
          style={{
            padding: 5,
            paddingTop: 17,
            marginRight: 17
          }}
          onPress={() => navigation.navigate("Deposithistory")}
        />
        </View>
        </View>
        </View>


        <View
          style={{
            flexDirection: 'row',
            gap: 5,
            padding: 5,
              marginLeft: 10,
              marginTop: 30,
          }}
        >
          <Text
            style={{
              fontSize: 18,
              color: 'gray',
              fontWeight: "300",
            }}
          >
            Total Balance
          </Text>
          <AntDesign name="eye" size={18} color="black" marginTop={2} />
        </View>

        <View
          style={{
            flexDirection: 'row',
            alignItems: 'center',
          }}
        >
          <Text
            style={{
              fontWeight: 'bold',
              paddingLeft: 13,
              paddingTop: 20,
              fontSize: 20,
              color: 'black',
            }}
          >
            {' '}
            {totalUsd.toFixed(4)} USDT
          </Text>
        </View>
        <View style={{
          flexDirection: "row",
           width: "100%",
           alignSelf: "center",
           paddingTop: 25}}>
          <TouchableOpacity style={{
            flex: 1,
            width: "30%",
            marginHorizontal: 0,
            alignSelf: "center"}}
            onPress={() => {
              if (currentUser) {
                navigation.navigate("Deposit");
              } else {
                setShowModal(true);
              }
            }}
          >
            <View style={{borderWidth: 0,
            alignItems: "center",
             justifyContent: "center",
              alignSelf: "center",
              width: 70,
              height: 70}}>
              <FontAwesome5 name="arrow-down" size={30} color="aqua" />
              <Text>Deposit</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity
            style={{flex: 1,
              width: "30%",
              marginHorizontal: 0,
              alignSelf: "center"}}
            onPress={() => handleNavigation("Withdraw")}
          >
            <View style={{borderWidth: 0,
            alignItems: "center",
             justifyContent: "center",
              alignSelf: "center",
              width: 70,
              height: 70}}>
              <FontAwesome5 name="arrow-up" size={30} color="aqua" />
              <Text>Withdraw</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity
            style={{flex: 1,
            width: "30%",
            marginHorizontal: 0,
            alignSelf: "center"}}
            onPress={() => handleNavigation("Exchange")}
          >
           <View style={{borderWidth: 0,
            alignItems: "center",
             justifyContent: "center",
              alignSelf: "center",
              width: 70,
              height: 70}}>
              <FontAwesome5 name="exchange-alt" size={30} color="aqua" />
              <Text>Exchange</Text>
            </View>
          </TouchableOpacity>
          <TouchableOpacity
            style={{flex: 1,
              width: "30%",
              marginHorizontal: 0,
              alignSelf: "center"}}
            onPress={() => handleNavigation("Loan")}
          >
            <View style={{borderWidth: 0,
            alignItems: "center",
             justifyContent: "center",
              alignSelf: "center",
              width: 70,
              height: 70}}>
              <Entypo name="wallet" size={30} color="aqua" />
              <Text>Loan</Text>
            </View>
            </TouchableOpacity>
            </View>

        <View
          style={{
            flexDirection: 'row',
            justifyContent: 'space-between',
            paddingHorizontal: 30,
            paddingTop: 30,
            paddingBottom: 30,
          }}
        >
          <Text
