import axios from "axios";
import {
  View,
  Text,
  Image,
  Pressable,
  TouchableOpacity,
  Button,
  Dimensions,
} from "react-native";
import React, { useEffect, useState } from "react";
import { SafeAreaView } from "react-native-safe-area-context";
import * as ImagePicker from "expo-image-picker";
import * as FileSystem from "expo-file-system";
import {
  Entypo,
  Ionicons,
  FontAwesome5,
  MaterialCommunityIcons,
} from "@expo/vector-icons";
import {
  GestureHandlerRootView,
  ScrollView,
  TextInput,
} from "react-native-gesture-handler";
import ImageSelect from "../components/ImageSelect";
import Toast from "react-native-toast-message";
import { api_url } from "../config";
import { useSelector } from "react-redux";

const USDTERC = ({ navigation }) => {
  const [depositAmount, setDepositAmount] = useState(0);

  // _______________________________________

  const [image, setImage] = useState(null);
  // const { userReducer } = useSelector((state) => state);
  const currentUser  = useSelector(
    (state) => state?.userReducer?.currentUser
  );
console.log("CURRENT USDTTRc",currentUser)
  const data = {
    userId: currentUser?._id,
    username: currentUser?.username,
    amount: depositAmount,
    currency: 'USDT-ERC',
  };
  const depositData = JSON.stringify(data);

  const openImagePickerAsync = async () => {
    let permissionResult =
      await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (permissionResult.granted === false) {
      alert('Permission to access camera roll is required!');
      return;
    }
    let pickerResult = await ImagePicker.launchImageLibraryAsync();
    setImage(pickerResult?.assets[0].uri);
  };

  const handleDeposit = async () => {
    if (!depositAmount || !image) {
      Toast.show({
        type: 'error',
        text1: 'Error',
        text2: 'Please enter the deposit amount and select an image.',
      });
      return;
    } else {
      try {
        const uploadResult = await FileSystem.uploadAsync(
          `${api_url}/deposit/createDeposit`,
          image,
          {
            httpMethod: 'POST',
            uploadType: FileSystem.FileSystemUploadType.MULTIPART,
            fieldName: 'demo_image',
            headers: {
              data: depositData,
            },
          }
        );
        let result = await JSON.parse(uploadResult.body);
        if (result.status === 200) {
          Toast.show({
            type: 'success',
            text1: 'Success',
            text2: 'Deposit request sent successfully.',
          });
          setImage(null);
          setDepositAmount(null);
          console.log('RESUUUUUULLLLTTT', result);
        }else{
          // console.log(first)
          console.log('RESUUUUUULLLLTTTERRRPRR', result);
          Toast.show({
            type: 'error',
            text1: 'Error',
            text2: `${result?.error?.message}`,
          });

        }
      } catch (e) {
        // Toast.show({
        //   type: "error",
        //   text1: "Error",
        //   text2: "Failed to send deposit request.",
        // });

        Toast.show({
          type: 'error',
          text1: 'Error',
          text2: `Something went wrong`,
        });
      }
    }
  };
  return (
    <GestureHandlerRootView>
      <SafeAreaView style={{
        backgroundColor: "white"
      }}>
      <ScrollView>
        <View
          style={{
            flex: 1,
            flexDirection: "row",
            alignItems: "center",
          }}
        >
          <Ionicons
            name="arrow-back-outline"
            size={35}
            color="black"
            style={{
              padding: 5,
              marginLeft: 10,
              marginTop: 10,
            }}
            onPress={() => navigation.navigate("Deposit")}
          />
          <Text
            style={{
              padding: 5,
              marginLeft: 5,
              marginTop: 10,
              fontSize: 20,
              fontWeight: "bold",
            }}
          >
            Deposit
          </Text>

          <FontAwesome5
            name="history"
            size={25}
            color="black"
            style={{
              padding: 5,
              marginLeft: 230,
            }}
            onPress={() => navigation.navigate("Deposithistory")}
          />
        </View>
        <View
          style={{
            flexDirection: "row",
            marginTop: 40,
            justifyContent: "space-between",
            alignItems: "center",
            paddingHorizontal: 19,
          }}
        >
          <Text
            style={{
              fontSize: 17,
              color: "gray",
            }}
          >
            Network
          </Text>
          <Image
            source={require("../assets/USDT.png")}
            style={{
              alignItems: "center",
              height: 30,
              width: 30,
              marginLeft: 190,
            }}
          />

          <Text
            style={{
              fontSize: 17,
              fontWeight: "600",
              color: "black",
            }}
          >
            USDT-ERC
          </Text>
        </View>

        <View
          style={{
            justifyContent: "center",
            alignItems: "center",
            marginBottom: 20,
          }}
        >
          <Image
            source={require("../assets/(new)ethqrcode.jpg")}
            style={{
              borderColor: "aqua",
              borderWidth: 2,
              width: 200,
              height: 200,
              marginTop: 20,
            }}
          />
          <Text
            style={{
              padding: 25,
              textAlign: "center",
              fontSize: 18,
              color: "gray",
              textDecorationLine: "underline",
            }}
            selectable={true}
          >
            0x4A1f2938AB5a0250E07e38f9a91774880Cef1C6d
          </Text>
          <Pressable
            style={{
              flexDirection: "row",
              justifyContent: "center",
              alignItems: "center",
              padding: 10,
              backgroundColor: "#0e1111",
              borderRadius: 25,
              width: 200,
            }}
            onPress={() => Clipboard.setString('mail@mail.com')}
          >
            <Text style={{ color: "white", marginRight: 5 }}>Copy</Text>
            <Ionicons name="ios-copy" size={24} color="white" />
          </Pressable>
        </View>
        <View>
          <Text
            style={{
              marginLeft: 21,
              fontSize: 15,
              color: "gray",
            }}
          >
            Deposit Amount
          </Text>
        </View>
        <View
          style={{
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <GestureHandlerRootView>
            <TextInput
              style={{
                borderWidth: 1,
                width: 400,
                padding: 15,
                marginTop: 10,
              }}
              placeholder="Please Enter Amount"
              value={depositAmount}
              onChangeText={(text) => setDepositAmount(text)}
              keyboardType="numeric"
            />
          </GestureHandlerRootView>
        </View>
        <View
          style={{
            marginTop: 20,
          }}
        >
          <Text
            style={{
              marginLeft: 21,
              fontSize: 15,
              color: "gray",
              paddingBottom: 15
            }}
          >
            Click to upload pictures
          </Text>
        </View>
        <View
          style={{
            alignSelf: "center",
            justifyContent: "center",
            alignItems: "center",
            paddingVertical: 20,
            width: "94%",
            backgroundColor: "#0e1111",
          }}
        >
          <View style={{ flex: 1, borderColor: "white", justifyContent: "center", padding: 30, borderRadius: 15, borderWidth: 2, borderStyle: "dashed"}}>
          <Entypo name="camera" size={30} color="white" alignSelf="center" onPress={() => openImagePickerAsync()} />
            {image && (
              <Image
                source={{ uri: image }}
                style={{ width: 200, height: 200 }}
              />
            )}
          </View>
        </View>
        <TouchableOpacity
          style={{
            flexDirection: "row",
            justifyContent: "center",
            alignItems: "center",
            alignSelf: "center",
            padding: 10,
            backgroundColor: "aqua",
            borderRadius: 25,
            width: "100%",
            marginTop: 20,
            marginBottom: "5%",
          }}
          onPress={handleDeposit}  
        >
          <Text style={{ color: "white", marginRight: 5 }}>Confirm recharge</Text>
          <Ionicons name="ios-send" size={24} color="white" />
        </TouchableOpacity>
      </ScrollView>
      </SafeAreaView>
    </GestureHandlerRootView>
  );
};

export default USDTERC;
